INSERT INTO movies (title, description, release_year, genre, director) VALUES
('Back to the Future', 'A teenager travels back in time to ensure his parents fall in love and save his own existence.', 1985, 'Science Fiction', 'Robert Zemeckis'),
('Fight Club', 'An insomniac office worker and a devil-may-care soap maker form an underground fight club.', 1999, 'Drama', 'David Fincher'),
('Eternal Sunshine of the Spotless Mind', 'A couple undergo a medical procedure to erase each other from their memories.', 2004, 'Romance/Sci-Fi', 'Michel Gondry'),
('Good Will Hunting', 'A janitor at M.I.T. has a gift for mathematics, and a psychologist tries to help him with his gift and the rest of his life.', 1997, 'Drama', 'Gus Van Sant'),
('Pulp Fiction', 'The lives of two mob hitmen, a boxer, and a pair of diner bandits intertwine in four tales of violence and redemption.', 1994, 'Crime/Drama', 'Quentin Tarantino'),
('Kung Fu Panda', 'A clumsy panda becomes an unlikely kung fu master in order to protect his valley from a villainous snow leopard.', 2008, 'Animation', 'Mark Osborne'),
('The Truman Show', 'A man discovers that his whole life is a reality TV show.', 1998, 'Drama/Comedy', 'Peter Weir'),
('The Dark Knight', 'Batman faces his greatest challenge as he takes on the Joker in a battle for Gotham City's soul.', 2008, 'Action/Drama', 'Christopher Nolan'),
('Se7en', 'Two detectives track a brilliant serial killer who bases his murders on the seven deadly sins.', 1995, 'Crime/Mystery', 'David Fincher'),
('Spider-Man 2', 'Peter Parker struggles to balance his life as Spider-Man with his personal responsibilities.', 2004, 'Action/Sci-Fi', 'Sam Raimi'),
('Everything Everywhere All at Once', 'A Chinese immigrant gets swept up in an insane adventure where she must connect different versions of herself to save the multiverse.', 2022, 'Action/Sci-Fi', 'Daniel Kwan, Daniel Scheinert'),
